const month = 3 

switch (month) {
    case 1:
        console.log("January");
        break;
    case 2:
        console.log("February");
        break;
    case 3:
        console.log("March");
        //break;
    case 4:
        console.log("April");
       // break; use for break the control flow 

    default:
        console.log("default case match");
        break;
}

